import { endpoints } from "../api/endpoints";


export const handleDownload = async (id) => {
    const authJWT = localStorage.getItem('authToken');
    try {
      const response = await fetch(`${endpoints.GETUSERFILE}${id}/`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${JSON.parse(authJWT).access}`
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `file_${id}`);
      document.body.appendChild(link);
      link.click();
      link.parentNode.removeChild(link);
    } catch (error) {
      console.error('Error:', error);
      alert(error.message);
    }
  };


  const handleDelete = async (id) => {
    try {
      if (!authJWT) {
        throw new Error('No auth token found');
      }

      const response = await fetch(`${endpoints.DELETEUSERFILE}${id}/`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${JSON.parse(authJWT).access}`
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      setData(data.filter(item => item.id !== id));
    } catch (error) {
      console.error('Error:', error);
      setError(error.message);
    }
  };
