import React, { useState } from 'react';
import './FileItem.css';
import { formatISODate } from '../utils/CommonHandles';
import Modal from './Modal';
import { endpoints } from '../api/endpoints';

const FileItem = ({ file, handleDownload, handleDelete, userId = '' }) => {
  const [showModal, setShowModal] = useState(false);
  const [comment, setComment] = useState(file.commentary || '');
  const authJWT = localStorage.getItem('authToken');

  const handleAddComment = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleSaveComment = () => {
    // Здесь можно сделать API запрос для сохранения комментария на сервере
    // Для простоты добавим комментарий локально

    const fetchData = async (file_id) => {
      try {
        if (!authJWT) {
          throw new Error('No auth token found');
        }

        const response = await fetch(`${endpoints.SETCOMMENT}${file_id}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${JSON.parse(authJWT).access}`
          }
        });

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const result = await response.json();
        setData(result);
      } catch (error) {
        setError(error.message);
      }
    }

    file.commentary = comment;
    setShowModal(false);
  };

  return (
    <div className='fileContainer' key={file.id}>
      <div className='fileName'>
        <a href="#" onClick={() => handleDownload(file.id)}>Имя: {file.filename}</a>
        <span>Дата загрузки: {formatISODate(file.uploaded_date)}</span>
        <span>Расширение: {file.extension}</span>
        {file.commentary && <span>Коментарий: {file.commentary}</span>}
        <span>Размер: {Math.round(file.size / 1024)}kb</span>
        {file.last_download_date && <span>Дата последнего скачивания: {formatISODate(file.last_download_date)}</span>}
      </div>
      <div className='fileActions'>
        <button className='delete-button' onClick={() => (!userId ? handleDelete(file.id) : handleDelete(userId, file.id))}>DELETE</button>
        <button className='coment-button' onClick={handleAddComment}>Add comment</button>
        <button className='share-button'>Share link</button>
      </div>
      <Modal show={showModal} handleClose={handleCloseModal} handleSave={handleSaveComment}>
        <textarea
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          placeholder="Add your comment here"
        />
      </Modal>
    </div>
  );
};

export default FileItem;
