import { createContext, useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import { endpoints } from '../api/endpoints'

import { jwtDecode } from 'jwt-decode'

const AuthContext = createContext()

export default AuthContext

export const AuthProvider = ({ children }) => {

    let [authTokens, setAuthTokens] = useState(() => localStorage.getItem('authToken') ? JSON.parse(localStorage.getItem('authToken')) : null)
    let [user, setUser] = useState(() => localStorage.getItem('authToken') ? jwtDecode(localStorage.getItem('authToken')) : null)

    const navigate = useNavigate()

    let loginUser = async (e) => {
        e.preventDefault()
        let response = await fetch(endpoints.GETTOKEN, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                username: e.target.username.value,
                password:  e.target.password.value
            })
        })

        let data = await response.json()

        if (response.status === 200) {
            setAuthTokens(data)
            setUser(jwtDecode(data.access))
            localStorage.setItem('authToken', JSON.stringify(data))
            navigate('/')
        } else {
            alert('Auth failed, check server')
        }
    }

    let logoutUser = () => {
        setAuthTokens(data)
            setUser(jwtDecode(data.access))
    }

    let contextData = {
        user: user,
        loginUser: loginUser
    }

    return (
        <AuthContext.Provider value={ contextData }>
            {children}
        </AuthContext.Provider>
    )
}
