import { endpoints } from "../api/endpoints";


export const handleDownload = async (id) => {
  const authJWT = localStorage.getItem('authToken');
  try {
    const response = await fetch(`${endpoints.GETUSERFILE}${id}/`, {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${JSON.parse(authJWT).access}`
      }
    });

    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }

    // Получение заголовка Content-Disposition
    const disposition = response.headers.get('Content-Disposition');
    let filename = `file_${id}`;
    if (disposition && disposition.includes('attachment')) {
      const filenameMatch = disposition.match(/filename="(.+)"/);
      if (filenameMatch.length === 2) {
        filename = filenameMatch[1];
      }
    }

    const blob = await response.blob();
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.setAttribute('download', filename);
    document.body.appendChild(link);
    link.click();
    link.parentNode.removeChild(link);
  } catch (error) {
    console.error('Error:', error);
    alert(error.message);
  }
};


export const formatISODate = (isoDateStr) => {
    const date = new Date(isoDateStr);

    const options = {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
        timeZoneName: 'short'
    };

    const formattedDate = date.toLocaleDateString('ru-RU', options);

    return formattedDate;
}
