import React, { useEffect, useState } from 'react';
import { endpoints } from '../api/endpoints';
import './AdminPage.css';

const AdminPage = () => {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const authJWT = localStorage.getItem('authToken');

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (!authJWT) {
          throw new Error('No auth token found');
        }

        const response = await fetch(endpoints.GETADMINDATA, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${JSON.parse(authJWT).access}`
          }
        });

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const result = await response.json();
        setData(result);
      } catch (error) {
        setError(error.message);
      }
    };

    fetchData();
  }, [authJWT]);

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`${endpoints.DELETEUSER}${id}/`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${JSON.parse(authJWT).access}`
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      setData(data.filter(user => user.user_info.id !== id));
    } catch (error) {
      setError(error.message);
    }
  };

  if (error) {
    return <div className="error-message">Error: {error}</div>;
  }

  if (data === null) {
    return <div className="loading-message">Loading...</div>;
  }

  if (data.length === 0) {
    return <div className="no-users-message">No users found</div>;
  }

  const formatSize = (sizeInKb) => {
    return (sizeInKb / (1024)).toFixed(2);
  };

  console.log(data)

  return (
    <div className="admin-page">
      <span>Зарегистрированные пользователи:</span>
      {data.map((user) => (
        <div className="user-card" key={user.user_info.username}>
          <span className="username">{user.user_info.username}</span>
          <span>{user.user_info.email}</span>
          <span className="role">{user.user_info.is_admin ? 'Администратор' : 'Пользователь'}</span>
          <span>Всего фаилов: {user.files_count}</span>
          {user.total_size && <span>Всего занято: {formatSize(user.total_size)}мб</span>}
          <button 
            className={`delete-button ${user.user_info.is_admin ? 'disabled' : ''}`} 
            onClick={() => !user.user_info.is_admin && handleDelete(user.user_info.id)}
            title={user.user_info.is_admin ? 'Администратора удалить нельзя' : 'Удалить пользователя'}
          >
            Delete
          </button>
        </div>
      ))}
      <hr />
    </div>
  );
};

export default AdminPage;
