import React, { useEffect, useState } from 'react';
import { endpoints } from '../api/endpoints';

const HomePage = () => {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const authJWT = localStorage.getItem('authToken');
        if (!authJWT) {
          throw new Error('No auth token found');
        }

        const response = await fetch(endpoints.GETUSERFILES, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${JSON.parse(authJWT).access}`
          }
        });

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const result = await response.json();
        setData(result);
      } catch (error) {
        setError(error.message);
      }
    };

    fetchData();
  }, []); // Пустой массив зависимостей указывает, что эффект должен выполняться только при монтировании

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!data) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <p>You are at HOMEPAGE!!!</p>
      {data.map((item) => 
        <div key={item.id}>
          <a>{item.filename}</a><span> | </span><a onClick={() => handleDelete(item.id)}>DELETE</a>
        </div>
      )}
    </div>
  );
};

const handleDelete = (id) => {
  console.log('DELETE', id)
}

export default HomePage;
