import React, { useEffect, useState } from 'react';
import { endpoints } from '../api/endpoints';

const HomePage = () => {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const authJWT = localStorage.getItem('authToken');

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (!authJWT) {
          throw new Error('No auth token found');
        }

        const response = await fetch(endpoints.GETUSERFILES, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${JSON.parse(authJWT).access}`
          }
        });

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const result = await response.json();
        setData(result);
      } catch (error) {
        setError(error.message);
      }
    };

    fetchData();
  }, [authJWT]); // Добавляем authJWT как зависимость

  const handleDelete = async (id) => {
    try {
      if (!authJWT) {
        throw new Error('No auth token found');
      }

      const response = await fetch(`${endpoints.DELETEUSERFILE}${id}/`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${JSON.parse(authJWT).access}`
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      // Удаляем элемент из состояния данных после успешного удаления
      setData(data.filter(item => item.id !== id));
    } catch (error) {
      console.error('Error:', error);
      setError(error.message);
    }
  };

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!data) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <p>You are at HOMEPAGE!!!</p>
      <p onClick={handle}>ADD NEW FILE</p>
      {data.map((item) => (
        <div key={item.id}>
          <a>{item.filename}</a><span> | </span><a href="#" onClick={() => handleDelete(item.id)}>DELETE</a>
        </div>
      ))}
    </div>
  );
};

export default HomePage;
