import React, { useEffect, useState } from 'react'
import { endpoints } from '../api/endpoints';

const AdminPage = () => {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const authJWT = localStorage.getItem('authToken');

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (!authJWT) {
          throw new Error('No auth token found');
        }

        const response = await fetch(endpoints.GETADMINDATA, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${JSON.parse(authJWT).access}`
          }
        });

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const result = await response.json();
        setData(result);
      } catch (error) {
        setError(error.message);
      }
    };

    fetchData();
  }, [authJWT])

  console.log(data)

  return (
    <div>
      <span>Зарегистрированные пользователи:</span>
      {data.map((user) => {
        <div>
          <span>{user.user_info.username}</span>
          <span>{user.email}</span>
          <span>{user.is_admin? 'Администратор' : 'Пользователь'}</span>
        </div>
      })}
      <hr/>
      
    </div>
    
  )
}

export default AdminPage
