import React from 'react';

const FileItem = ({ file, handleDownload, handleDelete, userId = '' }) => {
  return (
    <div className='fileContainer' key={file.id}>
      <div className='fileName'>
        <a href="#" onClick={() => handleDownload(file.id)}>{file.filename}</a>
        <span>Дата загрузки:{file.uploaded_date}</span>
        <span>Расширение{file.filesize} байт</span>
      </div>
      <div className='fileActions'>
        <a href="#" onClick={() => (!userId ? handleDelete(file.id) : handleDelete(userId, file.id))}>DELETE</a>
      </div>
    </div>
  );
};

export default FileItem;
